import React from 'react';
import { useState,useEffect } from 'react';

import './CourseTestimonials.css'

import Carousel from 'react-items-carousel';
import * as FontAwesome from "react-icons/fa";
import Testimonial from './courseTestimonial/CourseTestimonial'
import Spinner from '../spinner/fullpagespinner/FullPageSpinner'

const testimonials = (props) => {
    console.log(props)
    const [activeItemIndex, setActiveItemIndex] = useState(0);
    const [spinner, setspinner] = useState(false);


    const items= props.testimonials
    
      
    let sliderZero = false
    if(activeItemIndex === 0) 
      sliderZero = true
    
    console.log(activeItemIndex,items.length-1)
    let sliderMaximum = false
    
    if( activeItemIndex === items.length -1 )
      sliderMaximum = true 

    let amountofCards=3
    let dimensions=useWindowDimensions()
    if(dimensions.width<1100){
        amountofCards=2
      }

    if(dimensions.width<530){
      amountofCards=1
    }
    
    return (
        <div>
            {spinner ? <Spinner/> :
            <div className='Testimonials '>
                <div className='Testimonials__Carousel'>
                    <Carousel
                        slidesToScroll={2}
                        activeItemIndex={activeItemIndex}
                        requestToChangeActive={setActiveItemIndex} 
                        numberOfCards={3}
                        gutter={35}
                        //disableSwipe on touch device? 
                        >
                        {props.testimonials.map((testimonial)=>(
                            <Testimonial key={testimonial.id} AccData={testimonial}/>
                        ))}
                    </Carousel>
                </div>
            
                <div className='Slider-manupulator'>
                    <div>
                        <button disabled={sliderZero} onClick={()=>setActiveItemIndex(activeItemIndex-1)} className='Slider-manupulator__Button  Slider-manupulator__Button_Testimonials  Slider-manupulator__Button_left'>{<FontAwesome.FaLessThan className='falessThan'/>}</button>
                        <button disabled={sliderMaximum} onClick={()=>{setActiveItemIndex(activeItemIndex+1)}} className='Slider-manupulator__Button  Slider-manupulator__Button_Testimonials  Slider-manupulator__Button_right'>{<FontAwesome.FaGreaterThan className='falessThan'/>}</button>
                    </div>
                </div>
            </div>}

        </div>
    );
};
function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
      width,
      height
    };
}
  

  
function useWindowDimensions() {
    const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());
  
    useEffect(() => {
      function handleResize() {
        setWindowDimensions(getWindowDimensions());
      }
  
      window.addEventListener('resize', handleResize);
      return () => window.removeEventListener('resize', handleResize);
    }, []);
  
    return windowDimensions;
}
  
  
    
export default testimonials;
