import React from 'react';
import { useState,useEffect } from 'react';

import './CourseTestimonials.css'

import Carousel from 'react-items-carousel';
import * as FontAwesome from "react-icons/fa";
import Testimonial from './courseTestimonial/CourseTestimonial'
import Spinner from '.'

const testimonials = (props) => {
    console.log(props.testimonials)
    const [activeItemIndex, setActiveItemIndex] = useState(0);

    const items= [
        {id: 1},
        {id: 2},
        {id: 3},
        {id: 4},
        {id: 5},
        {id: 6},
        {id: 7},
        {id: 8},
        {id: 9},
        {id: 10},
        {id: 11},
        {id: 12}
        
    ]
      
    let sliderZero = false
    if(activeItemIndex === 0) 
      sliderZero = true
    
    let sliderMaximum = false
    if( activeItemIndex === items.length - 2 )
      sliderMaximum = true 

    let amountofCards=3
    let dimensions=useWindowDimensions()
    if(dimensions.width<1100){
        amountofCards=2
      }

    if(dimensions.width<530){
      amountofCards=1
    }
    
    return (
        <div>
            <div className='Testimonials '>
                <div className='Testimonials__Carousel'>
                  <Carousel
                      slidesToScroll={2}
                      activeItemIndex={activeItemIndex}
                      requestToChangeActive={setActiveItemIndex} 
                      numberOfCards={amountofCards}
                      gutter={35}
                      //disableSwipe on touch device? 
                      >
                      {items.map((item)=>( 
                      <Testimonial key={item.id}/>
                      ))}
                  </Carousel>
                </div>
            
                <div className='Slider-manupulator'>
                    <div>
                        <button disabled={sliderZero} onClick={()=>setActiveItemIndex(activeItemIndex-2)} className='Slider-manupulator__Button  Slider-manupulator__Button_Testimonials  Slider-manupulator__Button_left'>{<FontAwesome.FaLessThan className='falessThan'/>}</button>
                        <button disabled={sliderMaximum} onClick={()=>setActiveItemIndex(activeItemIndex+2)} className='Slider-manupulator__Button  Slider-manupulator__Button_Testimonials  Slider-manupulator__Button_right'>{<FontAwesome.FaGreaterThan className='falessThan'/>}</button>
                    </div>
                </div>
            </div>

        </div>
    );
};
function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;
    return {
      width,
      height
    };
}
  

  
function useWindowDimensions() {
    const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());
  
    useEffect(() => {
      function handleResize() {
        setWindowDimensions(getWindowDimensions());
      }
  
      window.addEventListener('resize', handleResize);
      return () => window.removeEventListener('resize', handleResize);
    }, []);
  
    return windowDimensions;
}
  
  
    
export default testimonials;
