import React, { Component } from 'react';
import axios from 'axios'
import Spinner from '../../spinner/fullpagespinner/FullPageSpinner'

class AddNews extends Component {
    state={
        spinner:false,
        NewsData:{
            Title:{
                value:'',
                validation:{
                    requeried:true,
                    minLength:3,
                    maxLength:10
                    },
                valid:false,
                touched:false,
            },
            Info:{
                value:'',
                validation:{
                    requeried:true,
                    minLength:3,
                    maxLength:10
                    },
                valid:false,
                touched:false,
            },
            
        },
        FormValidity:false,
    }
    submitNews=()=>{
        this.setState({spinner:true})
        axios.get('https://academy-4a97f.firebaseio.com/accounts.json').then((response)=>{
            console.log(response.data)
            for(let accounts in response.data){
                if(this.state.registerData.Login.value === response.data[accounts].Login){
                    this.setState({BusyLogin:true})
                    break
                }
            }
            if(this.state.FormValidity){    
                    const account={
                        Title:this.capitalize(this.state.registerData.Name.value),
                        Info:this.capitalize(this.state.registerData.Surname.value),
                        
                    }
                    this.cleanState()
                    axios.post('https://academy-4a97f.firebaseio.com/courses'+this.props.course+'.json',account).then(response=> {
                        this.setState({spinner:false,FormValidity:false})
                        })
                        .catch(()=>{
                            this.setState({spinner:false})
                            console.log('error')}
                        )
            }
            else{
                this.setState({spinner:false,FormValidity:false})
                this.cleanState()
            }
        })
    }
    cleanState(){
        for (let element in this.state.registerData){
            const updated = {
                ...this.state.registerData
            }                                           
            this.setState({registerData:updated})
        }
    }
    checkValidity(value,rules){
        let isValid=true;
        if(rules.requeried){
            isValid=value.trim() !=='' && isValid
        }
        if(rules.minLength){
            isValid=value.length>=rules.minLength && isValid
        }
        if(rules.maxLength){
            isValid=value.length <= rules.maxLength && isValid
        }
        return isValid
    }
    changeHandler=(event)=>{ 
        const updated={
            ...this.state.registerData
        }
        let updatedFormElement={
            ...updated[event.target.id]
        }
        console.log(this.state.registerData.Subject.value)
        
        updatedFormElement.value=event.target.value
        updatedFormElement.valid=this.checkValidity(updatedFormElement.value,updatedFormElement.validation)
        updatedFormElement.touched=true
        updated[event.target.id].value=updatedFormElement.value
        updated[event.target.id].valid=updatedFormElement.valid
        let formIsValid=true
        for(let inputId in updated){
            formIsValid = updated[inputId].valid && formIsValid
            // console.log('bu hammis',updated[inputId].valid)
        }
        
        this.setState({registerData:updated,FormValidity:formIsValid})  
    }
    render() {
        return (
            <div className='AddNews'>
                <form className='AddNews__Form'>
                    <ul>
                        <li>
                            <label for='title'>Title</label>
                            <textarea onChange={(event)=>this.changeHandler(event)} id='Title'/>
                        </li>
                        <li>
                            <label for='info'>News info</label>
                            <textarea onChange={(event)=>this.changeHandler(event)} id='Info'/> 
                        </li>
                    </ul>
                    <button onClick={this.submitNews} className='AddNews__Button'>Submit news</button>
                </form>
            </div>
        );
    }
}

export default AddNews;
