import React from 'react';
import './CourseTestimonial.css'
import axios from 'axios'

const CourseTestimonial = (props) => {
    let image
    useEffect(() => {
        axios
          .get('https://academy-4a97f.firebaseio.com/accounts.json')
          .then(response =>{
            for(let acc in response.data){
                if(props.AccData.Login===response.data[acc].Login){
                    
                }
            }
          });
        }, []);
    
    return (
        <div className='Testimonial-container'>
        <div className='Testmonial'>
            <img className='Client__Image' src={props.AccData.image ? props.AccData.image[Object.keys(props.AccData.image)[0]].file : props.AccData.Gender==='Male' ? require('./img/male.jpg'): require('./img/female.png')} alt='profileimg'/>
            <span className='Client__Name' >{props.AccData.Name}</span>
            <span className='Client__Login' >{props.AccData.Login}</span>
            <span className='Client__Type'>{props.AccData.kind}</span>
            <p className='Client__Comment'>{props.AccData.message}</p>
        </div>
    </div>
    );
};

export default CourseTestimonial;
