import React, { Component } from 'react';
import axios from 'axios'
import Spinner from '../../spinner/spinner'

class AddNews extends Component {
    state={
        spinner:false,
        NewsData:{
            Title:{
                value:'',
                validation:{
                    requeried:true,
                    minLength:3,
                    maxLength:10
                    },
                valid:false,
                touched:false,
            },
            Info:{
                value:'',
                validation:{
                    requeried:true,
                    minLength:3,
                    maxLength:10
                    },
                valid:false,
                touched:false,
            },
            
        },
        FormValidity:false,
        accountData:this.props.accountData
    }
    componentDidMount(){
        axios.get('https://academy-4a97f.firebaseio.com/accounts.json').then((response)=>{
            for(let element in response.data){
                let account={
                    ...this.state.accountData
                }
                if(this.state.accountData.Login===response.data[element].Login){
                    account.image=response.data[element].image
                    this.setState({accountData:account})
                }
            }
        })
    }
    submitNews=(event)=>{
        this.setState({spinner:true})
    
        event.preventDefault()
                
    }
    cleanState(){
        for (let element in this.state.registerData){
            const updated = {
                ...this.state.registerData
            }                                           
            this.setState({registerData:updated})
        }
    }
    checkValidity(value,rules){
        let isValid=true;
        if(rules.requeried){
            isValid=value.trim() !=='' && isValid
        }
        if(rules.minLength){
            isValid=value.length>=rules.minLength && isValid
        }
        if(rules.maxLength){
            isValid=value.length <= rules.maxLength && isValid
        }
        return isValid
    }
    changeHandler=(event)=>{ 
        const updated={
            ...this.state.NewsData
        }
        let updatedFormElement={
            ...updated[event.target.id]
        }
        console.log(updated,updatedFormElement)
        updatedFormElement.value=event.target.value
        updatedFormElement.valid=this.checkValidity(updatedFormElement.value,updatedFormElement.validation)
        updatedFormElement.touched=true
        updated[event.target.id].value=updatedFormElement.value
        updated[event.target.id].valid=updatedFormElement.valid
        let formIsValid=true
        for(let inputId in updated){
            formIsValid = updated[inputId].valid && formIsValid
            // console.log('bu hammis',updated[inputId].valid)
        }
        
        this.setState({registerData:updated,FormValidity:formIsValid})  
    }
    render() {
        return (
            <div className='AddNews'>
                {
                this.state.spinner ? <Spinner/>:
                <form className='AddNews__Form'>
                    <ul>
                        <li>
                            <label htmlFor='Title'>Title</label>
                            <textarea id='Info' onChange={(event)=>this.changeHandler(event)} id='Title'/>
                        </li>
                        <li>
                            <label id='Info' htmlFor='info'>News info</label>
                            <textarea onChange={(event)=>this.changeHandler(event)} id='Info'/> 
                        </li>
                    </ul>
                    <button onClick={(event)=>this.submitNews(event)} className='AddNews__Button'>Submit news</button>
                </form>
                }
            </div>
        );
    }
}

export default AddNews;
