    const [activeItemIndex, setActiveItemIndex] = useState(0);

    const items=[
        {id:1},
        {id:2},
        {id:3},
        {id:4},
        {id:5},
        {id:6}
    ]
    // props.testimonials.map((testi)=>console.log('testi'))
    return (
            <div className='Testimonials__Carousel'>
                  <Carousel
                      slidesToScroll={2}
                      activeItemIndex={0}
