import React, { Component } from 'react';

class AddNews extends Component {
    state={
        spinner:false,
        NewsData:{
            Title:{
                value:'',
                validation:{
                    requeried:true,
                    minLength:3,
                    maxLength:10
                    },
                valid:false,
                touched:false,
            },
            Info:{
                value:'',
                validation:{
                    requeried:true,
                    minLength:3,
                    maxLength:10
                    },
                valid:false,
                touched:false,
            },
            
        },
        FormValidity:false,
        BusyLogin:false
    }
    cleanState(){
        for (let element in this.state.NewsData){
            const updated = {
                ...this.state.NewsData
            }                                           
            this.setState({registerData:updated})
        }
    }
    render() {
        return (
            <div className='AddNews'>
                <form className='AddNews__Form'>
                    <ul>
                        <li>
                            <label for='title'>Title</label>
                            <textarea id='Title'/>
                        </li>
                        <li>
                            <label for='info'>News info</label>
                            <textarea id='Info'/> 
                        </li>
                    </ul>
                </form>
            </div>
        );
    }
}

export default AddNews;
